    HomeRecommend: HomeRecommend,
    HomeWeekend: HomeWeekend
  },
  data: function data() {
    return {
      city: '',
      swiperList: [],
      iconList: []
    };
  },
  methods: {
    getHomeInfo: function getHomeInfo() {
      axios.get('/api/index.json').then(this.getHomeInfoSucc);
